#!\/bin\/bash\n\n#Example Remediation Script for installing chrome on Linux machines\n#This script has been tested with Fedora 24 and Ubuntu 16.04.01 LTS\n#This script may not work on all systems. Modify to fit your needs\n\ncat \/etc\/*release* | grep -E '(Fedora|Red Hat)' >& \/dev\/null\nif [[ $? -eq 0 ]]; then\n#Add Chrome Repo:\necho \"[google-chrome]\nname=google-chrome - \\$basearch\nbaseurl=http:\/\/dl.google.com\/linux\/chrome\/rpm\/stable\/\\$basearch\nenabled=1\ngpgcheck=1\ngpgkey=https:\/\/dl-ssl.google.com\/linux\/linux_signing_key.pub\" > \/etc\/yum.repos.d\/google-chrome.repo\n#Install Chrome:\nyum install google-chrome-stable -y &> \/dev\/null\nfi\n\ncat \/etc\/*release* | grep -E '(Ubuntu|Debian)' >& \/dev\/null\nif [[ $? -eq 0 ]]; then\n\techo \"deb http:\/\/dl.google.com\/linux\/chrome\/deb\/ stable main\" >> \/etc\/apt\/sources.list.d\n\twget -q -O - https:\/\/dl-ssl.google.com\/linux\/linux_signing_key.pub | apt-key add -\n\tapt-get update\n\tapt-get install google-chrome-stable -y &> \/dev\/null\nfi\n
