#!\/bin\/bash\n\n#Example Test Script for installing chrome on Linux machines\n#This script has been tested with Fedora 24 and Ubuntu 16.04.01 LTS\n#This script may not work on all systems. Modify to fit your needs\n\nfunction Test_Chrome() {\n\tcat \/etc\/*release* | grep -E '(Fedora|Red Hat)' >& \/dev\/null\n\tif [[ $? -eq 0 ]]; then\n\t\t\trpm -qa | grep \"google-chrome-stable\" >& \/dev\/null\n\t\t\tif [[ $? -eq 0 ]]; then\n\t\t\t\techo \"Test_Chrome script has detected Chrome.\" 1>&2\n\t\t\t\treturn 0 #package is installed\n\t\t\telse\n\t\t\t\techo \"Test_Chrome Script failed! Remediation scipt needs to run\" 1>&2\n\t\t\t\treturn 1 #package is not installed\n\t\t\tfi\n\n\tfi\n\n\tcat \/etc\/*release* | grep -E '(Ubuntu|Debian)' &> \/dev\/null\n\tif [[ $? -eq 0 ]]; then\n\t\tdpkg -l | grep \"google-chrome-stable\" >& \/dev\/null\n\t\tif [[ $? -eq 0 ]]; then\n\t\t\t\techo \"Test_Chrome script has detected Chrome.\" 1>&2\n\t\t\t\treturn 0\n\t\t\telse\n\t\t\t\techo \"Test_Chrome Script failed! Remediation scipt needs to run\" 1>&2\n\t\t\t\treturn 1\n\t\t\tfi\n\tfi\n}\nTest_Chrome\nexit $?\n
